mapper.select:
select  from(
	select ceil(rownum /#{itemsPerPage})page,  from(
		select  from seller where seller_assign = 1 order by seller_store_no DESC) 
		) where page = #{page}

mapper.insert:

	select seller_store_no_seq.nextval from dual

insert into seller values(#{sellerStoreNo}, #{sellerStoreName}, #{sellerTaxId}, #{sellerIndustry}, #{sellerSubIndustry}, #{sellerZipcode}
, #{sellerAddress}, #{sellerSubAddress}, #{sellerStoreImage}, #{sellerProduct1}, #{sellerProduct2}, #{sellerProduct3}
, #{sellerIntroduction}, 0, #{memberId})

mapper.select:
select  from(
	select ceil(rownum /#{itemPerPage})page,  from(
		select  from seller where seller_assign = 0 order by seller_store_no DESC) 
		) where page = #{page}

mapper.update:
update 	seller
set			seller_assign=1
where 	seller_store_no=#{sellerStoreNo}
